import React, { useState, useRef, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './styles/Navbar.css';

function Navbar() {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);

  const handleDropdownToggle = () => {
    setIsDropdownOpen(prevState => !prevState);
  };

  const handleClickOutside = (event) => {
    if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
      setIsDropdownOpen(false);
    }
  };

  useEffect(() => {
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <nav className="navbar">
      <ul>
        <li><Link to="/" className="nav-link">Home</Link></li>
        <li><Link to="/about" className="nav-link">About</Link></li>
        <li><Link to="/services" className="nav-link">Services</Link></li>
        <li className="dropdown" ref={dropdownRef}>
          <button 
            className="nav-link dropdown-toggle" 
            onClick={handleDropdownToggle}
          >
            Portfolio
          </button>
          {isDropdownOpen && (
            <ul className="dropdown-menu">
              <li><Link to="/education" className="dropdown-link">Education</Link></li>
              <li><Link to="/skills" className="dropdown-link">Skills</Link></li>
              <li><Link to="/experience" className="dropdown-link">Experience</Link></li>
              <li><Link to="/work" className="dropdown-link">Work</Link></li>
            </ul>
          )}
        </li>
        <li><Link to="/contact" className="nav-link">Contact</Link></li>
      </ul>
    </nav>
  );
}

export default Navbar;








