import React from 'react';
import './styles/About.css';

function About() {
  return (
    <section className="about">
      <h2>About Me</h2>
      <div className="about-content">
        <p>Hello! I'm <strong>Your Name</strong>, a passionate Full Stack Developer with a knack for building web applications that provide excellent user experiences. With a background in Computer Science and hands-on experience with modern web technologies, I enjoy turning complex problems into simple, beautiful, and intuitive designs.</p>
        <p>In my current role at <strong>Company Name</strong>, I work on developing scalable web applications using React.js, Node.js, and various other technologies. I thrive in environments that challenge my skills and allow me to grow professionally.</p>
        <p>In my free time, I enjoy contributing to open-source projects, exploring new technologies, and staying up-to-date with the latest industry trends.</p>
        <p>Let's connect and create something amazing together!</p>
      </div>
    </section>
  );
}

export default About;
